import { createReducer } from "@reduxjs/toolkit";

const initialStates = {
  userName: "",
  token:""
};
export const userReducer = createReducer(initialStates, {
  setUser: (state, action) => {
    state.userName = action.payload.userName,
    state.token=action.payload.token
  }
  
});
