import React, { useState } from 'react';
import 'bootstrap/dist/css/bootstrap.min.css'; // Import Bootstrap CSS
import { Container, Form, Button } from 'react-bootstrap'; // Import Bootstrap components

const ShortenLinkForm = () => {
  const [link, setLink] = useState('');
  const [shortenedLink, setShortenedLink] = useState('');

  const handleLinkChange = (e) => {
    setLink(e.target.value);
  };

  const handleShortenLink = (e) => {
    e.preventDefault();
    // Shorten the link logic here
    setShortenedLink('Shortened link goes here');
  };

  return (
    <Container className="d-flex align-items-center justify-content-center min-vh-100">
      <div className="bg-white p-5 rounded-lg shadow-lg">
        <h1 className="text-3xl font-bold mb-4 text-center">Shorten Link</h1>
        <Form className="space-y-3">
          <Form.Group controlId="link">
            <Form.Label className="text-gray-800 font-weight-bold mb-2">Original Link</Form.Label>
            <Form.Control
              type="text"
              placeholder="Enter the link you want to shorten"
              value={link}
              onChange={handleLinkChange}
            />
          </Form.Group>
          <Button
            variant="dark"
            className="w-100 rounded-pill text-xl font-medium"
            onClick={handleShortenLink}
          >
            Shorten Link
          </Button>
          <Form.Group controlId="shortenedLink">
            <Form.Label className="text-gray-800 font-weight-bold mb-2">Shortened Link</Form.Label>
            <Form.Control
              type="text"
              placeholder="Shortened link"
              value={shortenedLink}
              readOnly
            />
          </Form.Group>
        </Form>
      </div>
    </Container>
  );
};

export default ShortenLinkForm;
