import React, { useState } from 'react';

const ShortenLinkForm = () => {
  const [link, setLink] = useState('');
  const [shortenedLink, setShortenedLink] = useState('');

  const handleLinkChange = (e) => {
    setLink(e.target.value);
  };

  const handleShortenLink = (e) => {
    e.preventDefault();
    // Shorten the link logic here
    setShortenedLink('Shortened link goes here');
  };

  return (
    <div className="container d-flex align-items-center justify-content-center min-vh-100">
      <div className="bg-white p-5 rounded-lg shadow-lg">
        <h1 className="text-3xl font-bold mb-4 text-center">Shorten Link</h1>
        <form className="space-y-3">
          <div className="form-group">
            <label className="text-gray-800 font-weight-bold mb-2" htmlFor="link">
              Original Link
            </label>
            <input
              type="text"
              id="link"
              className="form-control"
              placeholder="Enter the link you want to shorten"
              value={link}
              onChange={handleLinkChange}
            />
          </div>
          <button
            type="button"
            className="btn btn-dark w-100 rounded-pill text-xl font-medium"
            onClick={handleShortenLink}
          >
            Shorten Link
          </button>
          <div className="form-group">
            <label className="text-gray-800 font-weight-bold mb-2" htmlFor="shortenedLink">
              Shortened Link
            </label>
            <input
              type="text"
              id="shortenedLink"
              className="form-control"
              placeholder="Shortened link"
              value={shortenedLink}
              readOnly
            />
          </div>
        </form>
      </div>
    </div>
  );
};

export default ShortenLinkForm;
